import React from "react";

const ProductCardComp = ({ name, price, checkClicked }) => {
  const handleIsClicked = () => {
    switch (isClicked) {
      case false:
        checkClicked(false);
        break;
      case true:
        checkClicked(true);
        break;
    }
  };
  return (
    <div className="product-card" onClick={handleIsClicked}>
      <div className="card-head">
        <img src="../../static/images/default.svg" />
      </div>
      <div className="card-body">
        <p>{name}</p>
        <p>{price}</p>
      </div>
    </div>
  );
};

export default ProductCardComp;
