import React, { useState, useEffect } from "react";
import Header from "../components/layout/header";
import Footer from "../components/layout/footer";
import Input from "../components/form/input";
import Button from "../components/form/button";
import Card from "../components/card/product";

const ProductListPage = () => {
  const [productList, setProductList] = useState([
    { idx: 1, name: "새우깡", price: "1500" },
    { idx: 2, name: "고구마깡", price: "2000" },
    { idx: 3, name: "감자깡", price: "1800" },
    { idx: 4, name: "다이제스티", price: "10000" },
    { idx: 5, name: "초코파이", price: "5000" },
  ]);
  const [searchVal, setSearchVal] = useState("");

  const handleChange = (name, value) => {
    setSearchVal(value);
  };

  const handleClick = () => {
    let item = { idx: 6, name: "몽쉘", price: "4000" };
    setProductList(productList.concat(item));
  };

  return (
    <>
      <Header />
      <div className="list-wrapper">
        <div className="container">
          <div className="con-head">
            <Input
              label="검색"
              type="text"
              name="search"
              changeCB={handleChange}
            />
            <Button btnColor="btn-black" clickCB={handleClick}>
              등 록
            </Button>
          </div>
          <div className="con-body">
            <div className="list">
              {productList.map((item) => {
                <Card name={item.name} price={item.price} />;
              })}
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default ProductListPage;
