import React from "react";

const ProductCardComp = ({ name, price, handleIsClicked }) => {
  const onSelect = (e) => {
    console.log(document.querySelector(".product-name").value);
    /* handleIsClicked(document.querySelector(".product-name").value); */
  };
  return (
    <div className="product-card" onClick={onSelect}>
      <div className="card-head">
        <img src="../../static/images/default.svg" />
      </div>
      <div className="card-body">
        <p className="product-name">{name}</p>
        <p>{price}</p>
      </div>
    </div>
  );
};

export default ProductCardComp;
